import Swal from "sweetalert2";

const WrongAlert = ({ title = "Error", message = "Ocurrió un error inesperado"}) => {
  // Crear un componente que renderiza null pero ejecuta el efecto
  setTimeout(() => {
    Swal.fire({
      icon: "error",
      title: title,
      text: message,
      confirmButtonColor: "#d33",
      confirmButtonText: "Entendido",
      allowOutsideClick: true,
      didClose: () => {
        // Asegurar que la alerta se cierre completamente
        Swal.close();
      }
    });
  }, 100);
  
  return null;
};

export default WrongAlert;
