import React, { useState, useEffect } from "react";
import "./ResetPasswordPage.css";
import ImgHandsBackground from "../../assets/photos/manos-voluntariado-bg.jpg";
import { useNavigate, useLocation } from "react-router-dom";
import WrongAlert from "../../components/alerts/WrongAlert";
import SuccessAlert from "../../components/alerts/SuccessAlert";
import api from "../../config/AxiosConfig";

function ResetPasswordPage() {
  const [code, setCode] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();
  const [token, setToken] = useState("");

  useEffect(() => {
    // Intentar obtener el token del localStorage primero
    const storedToken = localStorage.getItem('reset_password_token');

    if (storedToken) {
      setToken(storedToken);
    } else {
      // Si no está en localStorage, intentar obtenerlo de la URL (fallback)
      const queryParams = new URLSearchParams(location.search);
      const tokenParam = queryParams.get("token");
      if (tokenParam) {
        setToken(tokenParam);
        // Opcional: guardar en localStorage para futuras referencias
        localStorage.setItem('reset_password_token', tokenParam);
      }
    }

    // Limpiar la URL para que no muestre el token
    if (location.search.includes('token=')) {
      navigate('/reset-password', { replace: true });
    }
  }, [location, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones básicas
    if (!code.trim()) {
      setError("Por favor ingresa el código de verificación");
      return;
    }

    if (!password.trim()) {
      setError("Por favor ingresa una nueva contraseña");
      return;
    }

    if (password !== confirmPassword) {
      setError("Las contraseñas no coinciden");
      return;
    }

    if (password.length < 6) {
      setError("La contraseña debe tener al menos 6 caracteres");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Conectar con el endpoint del backend para resetear la contraseña
      const response = await api.post(
        "/auth/restablecer",
        { 
          token: code, // El código de verificación que recibió por correo
          nuevaContrasena: password
        },
        {
          headers: {
            Authorization: `Bearer ${token}` // El token JWT que se obtuvo de la URL o localStorage
          }
        }
      );

      setSuccess("¡Contraseña actualizada correctamente!");

      // Limpiar el token del localStorage
      localStorage.removeItem('reset_password_token');

      // Redirigir al login después de 2 segundos
      setTimeout(() => {
        navigate("/login");
      }, 2000);

    } catch (err) {
      if (err.response && err.response.status === 404) {
        setError("Código de verificación inválido o expirado");
      } else if (err.response && err.response.data && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError("Error al actualizar la contraseña. Inténtalo de nuevo más tarde.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-password-page">
      <div className="reset-password-background">
        <img src={ImgHandsBackground} alt="Manos voluntariado" />
      </div>
      <div className="reset-password-container">
        <h2 className="reset-password-title">Restablecer Contraseña</h2>
        <p className="reset-password-description">
          Ingresa el código de verificación que recibiste por correo electrónico y tu nueva contraseña.
        </p>

        {error && <WrongAlert message={error} />}
        {success && <SuccessAlert message={success} />}

        <form onSubmit={handleSubmit} className="reset-password-form">
          <div className="form-group">
            <label htmlFor="code">Código de verificación</label>
            <input
              type="text"
              id="code"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder="Ingresa el código de 6 dígitos"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Nueva contraseña</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Ingresa tu nueva contraseña"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirmar contraseña</label>
            <input
              type="password"
              id="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Confirma tu nueva contraseña"
              disabled={loading}
            />
          </div>

          <button 
            type="submit" 
            className="reset-password-button"
            disabled={loading}
          >
            {loading ? "Procesando..." : "Cambiar Contraseña"}
          </button>
        </form>

        <div className="reset-password-back-link">
          <p>
            <a href="/login">Volver al inicio de sesión</a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default ResetPasswordPage;
